import React, { useEffect, useMemo, useState } from "react";
import { api } from "./api";

export default function ItemsPage({ onLogout }) {
  const [items, setItems] = useState([]);
  const [cart, setCart] = useState(null);
  const [orders, setOrders] = useState([]);
  const [showOrders, setShowOrders] = useState(false);
  const [loading, setLoading] = useState(false);

  const loadItems = async () => {
    const res = await api.get("/items");
    setItems(res.data || []);
  };

  // âœ… Load user's active cart (requires backend GET /carts/me)
  const loadMyCart = async () => {
    try {
      const res = await api.get("/carts/me");
      setCart(res.data);
    } catch {
      setCart(null);
    }
  };

  useEffect(() => {
    loadItems();
    loadMyCart();
  }, []);

  const addToCart = async (itemId) => {
    try {
      setLoading(true);
      const res = await api.post("/carts", { itemId });
      setCart(res.data);
    } catch (e) {
      console.error(e);
      window.alert("Failed to add item. Please login again.");
    } finally {
      setLoading(false);
    }
  };

  const removeFromCart = async (itemId) => {
    try {
      setLoading(true);
      const res = await api.delete(`/carts/items/${itemId}`);
      setCart(res.data);
    } catch (e) {
      console.error(e);
      const msg = e?.response?.data?.error || e?.response?.data || e.message;
      window.alert(msg);
    } finally {
      setLoading(false);
    }
  };

  const checkout = async () => {
    if (!cart?.cart?.id) {
      window.alert("Cart is empty.");
      return;
    }
    try {
      setLoading(true);
      await api.post("/orders", { cartId: cart.cart.id });
      window.alert("Order successful");
      setCart(null);
      await loadMyCart();
      // refresh orders if panel open
      if (showOrders) await fetchOrders();
    } catch (e) {
      console.error(e);
      const msg = e?.response?.data?.error || e?.response?.data || e.message;
      window.alert(msg);
    } finally {
      setLoading(false);
    }
  };

  const fetchOrders = async () => {
    try {
      setLoading(true);
      const res = await api.get("/orders");
      setOrders(res.data || []);
    } catch (e) {
      console.error(e);
      window.alert("Failed to fetch orders.");
    } finally {
      setLoading(false);
    }
  };

  const toggleOrderHistory = async () => {
    const next = !showOrders;
    setShowOrders(next);
    if (next) {
      await fetchOrders();
    }
  };

  const handleLogout = () => {
    setCart(null);
    setOrders([]);
    setShowOrders(false);
    onLogout();
  };

  const itemNameById = useMemo(() => new Map(items.map((i) => [i.id, i.name])), [items]);

  const getEmoji = (name) => {
    const n = (name || "").toLowerCase();
    if (n.includes("apple")) return "ðŸŽ";
    if (n.includes("milk")) return "ðŸ¥›";
    if (n.includes("bread")) return "ðŸž";
    if (n.includes("egg")) return "ðŸ¥š";
    if (n.includes("rice")) return "ðŸš";
    return "ðŸ›’";
  };

  // âœ… helpers for order UI
  const formatDate = (value) => {
    if (!value) return "â€”";
    const d = new Date(value);
    if (Number.isNaN(d.getTime())) return String(value);
    return d.toLocaleString();
  };

  // If backend doesn't send status, show a default
  const getOrderStatus = (o) => o.status || "PLACED";

  const styles = {
    page: {
      minHeight: "100vh",
      background: "linear-gradient(180deg,#f6f8ff,#ffffff)",
      fontFamily: "Inter, Arial, sans-serif",
      padding: 16,
    },
    wrap: { maxWidth: 1100, margin: "0 auto" },

    headerBar: {
      display: "flex",
      alignItems: "center",
      justifyContent: "space-between",
      gap: 12,
      flexWrap: "wrap",
      marginBottom: 16,
    },

    brand: { display: "flex", alignItems: "center", gap: 10 },
    logo: {
      width: 44,
      height: 44,
      borderRadius: 12,
      background: "linear-gradient(135deg,#6366f1,#22c55e)",
      display: "grid",
      placeItems: "center",
      color: "#fff",
      fontWeight: 900,
      flex: "0 0 44px",
    },
    title: { margin: 0, fontSize: 22, fontWeight: 900, color: "#111827" },
    sub: { margin: "4px 0 0", color: "#6b7280", fontSize: 13 },

    actionsRow: {
      display: "flex",
      gap: 10,
      flexWrap: "wrap",
      width: "fit-content",
    },

    btn: {
      border: "1px solid #e5e7eb",
      background: "#fff",
      borderRadius: 12,
      padding: "10px 12px",
      fontWeight: 900,
      cursor: "pointer",
      boxShadow: "0 6px 16px rgba(17,24,39,0.06)",
      whiteSpace: "nowrap",
    },
    btnPrimary: {
      background: "linear-gradient(135deg,#6366f1,#8b5cf6)",
      color: "#fff",
      border: "none",
    },
    btnDanger: {
      background: "linear-gradient(135deg,#ef4444,#f97316)",
      color: "#fff",
      border: "none",
    },
    btnGhost: {
      background: "#fff",
      border: "1px solid #e5e7eb",
    },
    disabled: { opacity: 0.6, cursor: "not-allowed" },

    mainGrid: {
      display: "grid",
      gridTemplateColumns: "1.2fr 0.8fr",
      gap: 14,
    },

    card: {
      background: "#fff",
      border: "1px solid #eef2ff",
      borderRadius: 18,
      boxShadow: "0 12px 26px rgba(17,24,39,0.08)",
      padding: 16,
    },

    sectionTitle: { margin: 0, fontSize: 18, fontWeight: 900, color: "#111827" },
    hint: { margin: "6px 0 0", color: "#6b7280", fontSize: 13 },

    badge: {
      fontSize: 12,
      fontWeight: 900,
      color: "#4338ca",
      background: "#eef2ff",
      padding: "4px 10px",
      borderRadius: 999,
      whiteSpace: "nowrap",
    },

    itemsGrid: {
      marginTop: 12,
      display: "grid",
      gridTemplateColumns: "repeat(2, minmax(0, 1fr))",
      gap: 12,
    },

    itemCard: {
      display: "flex",
      alignItems: "center",
      gap: 12,
      border: "1px solid #e5e7eb",
      borderRadius: 14,
      padding: 12,
      cursor: "pointer",
      background: "#fff",
    },

    icon: {
      width: 44,
      height: 44,
      borderRadius: 12,
      border: "1px solid #e5e7eb",
      display: "grid",
      placeItems: "center",
      fontSize: 24,
      background: "#f9fafb",
      flex: "0 0 44px",
    },

    name: { margin: 0, fontWeight: 900, color: "#111827" },
    meta: { margin: "3px 0 0", color: "#6b7280", fontSize: 12 },

    cartRow: {
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      gap: 10,
      padding: "10px 0",
      borderBottom: "1px solid #f3f4f6",
    },

    removeBtn: {
      borderRadius: 10,
      padding: "8px 10px",
      border: "1px solid #fecaca",
      background: "#fff5f5",
      color: "#b91c1c",
      fontWeight: 900,
      cursor: "pointer",
      whiteSpace: "nowrap",
    },

    // Orders UI
    ordersPanel: {
      marginTop: 14,
      paddingTop: 14,
      borderTop: "1px dashed #e5e7eb",
    },
    orderRow: {
      display: "flex",
      justifyContent: "space-between",
      gap: 12,
      padding: "10px 0",
      borderBottom: "1px solid #f3f4f6",
      alignItems: "center",
      flexWrap: "wrap",
    },
    statusPill: (status) => ({
      fontSize: 12,
      fontWeight: 900,
      padding: "6px 10px",
      borderRadius: 999,
      background:
        status === "PLACED"
          ? "#ecfeff"
          : status === "DELIVERED"
          ? "#ecfdf5"
          : "#fff7ed",
      color:
        status === "PLACED"
          ? "#155e75"
          : status === "DELIVERED"
          ? "#065f46"
          : "#9a3412",
      border: "1px solid #e5e7eb",
      whiteSpace: "nowrap",
    }),
  };

  return (
    <div style={styles.page}>
      <style>{`
        @media (max-width: 900px) {
          .mainGrid { grid-template-columns: 1fr !important; }
          .itemsGrid { grid-template-columns: 1fr !important; }
          .actionsRow { width: 100% !important; justify-content: flex-start !important; }
        }
      `}</style>

      <div style={styles.wrap}>
        {/* header */}
        <div style={styles.headerBar}>
          <div style={styles.brand}>
            <div style={styles.logo}>SC</div>
            <div>
              <p style={styles.title}>Shopping Cart</p>
              <p style={styles.sub}>Add items, view cart, and checkout</p>
            </div>
          </div>

          <div className="actionsRow" style={styles.actionsRow}>
            <button
              onClick={checkout}
              disabled={loading}
              style={{ ...styles.btn, ...styles.btnPrimary, ...(loading ? styles.disabled : {}) }}
            >
              âœ… Checkout
            </button>

            <button
              onClick={toggleOrderHistory}
              disabled={loading}
              style={{ ...styles.btn, ...styles.btnGhost, ...(loading ? styles.disabled : {}) }}
            >
              ðŸ“¦ {showOrders ? "Hide Orders" : "Order History"}
            </button>

            <button onClick={handleLogout} style={{ ...styles.btn, ...styles.btnDanger }}>
              ðŸšª Logout
            </button>
          </div>
        </div>

        {/* main grid */}
        <div className="mainGrid" style={styles.mainGrid}>
          {/* Items */}
          <div style={styles.card}>
            <div style={{ display: "flex", justifyContent: "space-between", alignItems: "baseline", gap: 10 }}>
              <div>
                <p style={styles.sectionTitle}>Items</p>
                <p style={styles.hint}>Tap an item to add to cart.</p>
              </div>
              <span style={styles.badge}>{items.length} items</span>
            </div>

            <div className="itemsGrid" style={styles.itemsGrid}>
              {items.map((it) => (
                <div
                  key={it.id}
                  onClick={() => addToCart(it.id)}
                  style={{ ...styles.itemCard, opacity: loading ? 0.75 : 1 }}
                >
                  <div style={styles.icon}>{getEmoji(it.name)}</div>
                  <div>
                    <p style={styles.name}>{it.name}</p>
                    <p style={styles.meta}>Item ID: {it.id}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Cart + Orders panel */}
          <div style={styles.card}>
            <div style={{ display: "flex", justifyContent: "space-between", alignItems: "baseline", gap: 10 }}>
              <div>
                <p style={styles.sectionTitle}>Cart</p>
                <p style={styles.hint}>Remove items if needed.</p>
              </div>
              <span style={styles.badge}>{cart?.cart_items?.length || 0} in cart</span>
            </div>

            {!cart?.cart_items?.length ? (
              <div style={{ marginTop: 12, color: "#6b7280", fontSize: 13 }}>Your cart is empty.</div>
            ) : (
              <div style={{ marginTop: 12 }}>
                {cart.cart_items.map((ci, idx) => {
                  const name = itemNameById.get(ci.item_id) || "Unknown";
                  return (
                    <div key={ci.id || idx} style={styles.cartRow}>
                      <div style={{ display: "flex", alignItems: "center", gap: 10 }}>
                        <div style={styles.icon}>{getEmoji(name)}</div>
                        <div>
                          <div style={{ fontWeight: 900 }}>{name}</div>
                          <div style={{ fontSize: 12, color: "#6b7280" }}>Item #{ci.item_id}</div>
                        </div>
                      </div>

                      <button
                        onClick={() => removeFromCart(ci.item_id)}
                        disabled={loading}
                        style={{ ...styles.removeBtn, ...(loading ? styles.disabled : {}) }}
                      >
                        Remove
                      </button>
                    </div>
                  );
                })}
              </div>
            )}

            {/* âœ… Professional Order History panel (NO POPUP) */}
            {showOrders && (
              <div style={styles.ordersPanel}>
                <div style={{ display: "flex", justifyContent: "space-between", alignItems: "baseline", gap: 10 }}>
                  <p style={{ ...styles.sectionTitle, fontSize: 16 }}>Order History</p>
                  <span style={styles.badge}>{orders.length} orders</span>
                </div>

                {!orders.length ? (
                  <div style={{ marginTop: 10, color: "#6b7280", fontSize: 13 }}>No orders yet.</div>
                ) : (
                  <div style={{ marginTop: 10 }}>
                    {orders
                      .slice()
                      .reverse()
                      .map((o) => {
                        const status = getOrderStatus(o);
                        return (
                          <div key={o.id} style={styles.orderRow}>
                            <div>
                              <div style={{ fontWeight: 900, color: "#111827" }}>Order #{o.id}</div>
                              <div style={{ fontSize: 12, color: "#6b7280" }}>
                                Date: {formatDate(o.created_at || o.createdAt)}
                              </div>
                              {o.cart_id != null && (
                                <div style={{ fontSize: 12, color: "#6b7280" }}>Cart ID: {o.cart_id}</div>
                              )}
                            </div>

                            <div style={styles.statusPill(status)}>{status}</div>
                          </div>
                        );
                      })}
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
