import React, { useEffect, useMemo, useState } from "react";
import { api } from "./api";

export default function ItemsPage({ onLogout }) {
  const [items, setItems] = useState([]);
  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(false);

  const loadItems = async () => {
    const res = await api.get("/items");
    setItems(res.data);
  };

  useEffect(() => {
    loadItems();
  }, []);

  const addToCart = async (itemId) => {
    try {
      setLoading(true);
      const res = await api.post("/carts", { itemId });
      setCart(res.data);
    } catch (e) {
      console.error(e);
      window.alert("Failed to add item to cart. Please login again.");
    } finally {
      setLoading(false);
    }
  };

  const removeFromCart = async (itemId) => {
    try {
      setLoading(true);
      const token = localStorage.getItem("token");

      const res = await api.delete(`/carts/items/${itemId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setCart(res.data);
    } catch (e) {
      console.error(e);
      const msg = e?.response?.data?.error || e?.response?.data || e.message;
      window.alert(msg);
    } finally {
      setLoading(false);
    }
  };

  const showCart = () => {
    if (!cart?.cart_items?.length) {
      window.alert("Cart is empty.");
      return;
    }

    const itemNameById = new Map(items.map((i) => [i.id, i.name]));
    const lines = cart.cart_items.map((ci) => {
      const name = itemNameById.get(ci.item_id) || "Unknown";
      return `(${ci.cart_id}, ${ci.item_id}) - ${name}`;
    });

    window.alert(lines.join("\n"));
  };

  const showOrderHistory = async () => {
    try {
      const res = await api.get("/orders");
      const orders = res.data || [];
      const ids = orders.map((o) => o.id).join(", ");
      window.alert(ids ? `Order IDs: ${ids}` : "No orders yet.");
    } catch (e) {
      console.error(e);
      window.alert("Failed to fetch orders.");
    }
  };

  const checkout = async () => {
    if (!cart?.cart?.id) {
      window.alert("Cart is empty.");
      return;
    }
    try {
      setLoading(true);
      await api.post("/orders", { cartId: cart.cart.id });
      window.alert("Order successful");
      setCart(null);
    } catch (e) {
      console.error(e);
      const msg = e?.response?.data?.error || e?.response?.data || e.message;
      window.alert(msg);
    } finally {
      setLoading(false);
    }
  };

  const itemNameById = useMemo(() => new Map(items.map((i) => [i.id, i.name])), [items]);

  // Simple "images" using emojis (no assets needed)
  const getItemEmoji = (name) => {
    const n = (name || "").toLowerCase();
    if (n.includes("apple")) return "ðŸŽ";
    if (n.includes("milk")) return "ðŸ¥›";
    if (n.includes("bread")) return "ðŸž";
    if (n.includes("egg")) return "ðŸ¥š";
    if (n.includes("rice")) return "ðŸš";
    return "ðŸ›’";
  };

  // nice gradient colors per item card
  const getItemGradient = (name) => {
    const n = (name || "").toLowerCase();
    if (n.includes("apple")) return "linear-gradient(135deg,#ffe3e3,#fff)";
    if (n.includes("milk")) return "linear-gradient(135deg,#e6f4ff,#fff)";
    if (n.includes("bread")) return "linear-gradient(135deg,#fff1da,#fff)";
    if (n.includes("egg")) return "linear-gradient(135deg,#fff7d6,#fff)";
    if (n.includes("rice")) return "linear-gradient(135deg,#e9ffe9,#fff)";
    return "linear-gradient(135deg,#f1f5ff,#fff)";
  };

  // styles (inline to keep it simple)
  const styles = {
    page: {
      minHeight: "100vh",
      background: "linear-gradient(180deg,#f6f8ff,#ffffff)",
      padding: "28px 16px",
      fontFamily: "Inter, Arial, sans-serif",
      color: "#111827",
    },
    shell: {
      maxWidth: 980,
      margin: "0 auto",
    },
    header: {
      display: "flex",
      alignItems: "center",
      justifyContent: "space-between",
      gap: 12,
      marginBottom: 18,
    },
    brand: {
      display: "flex",
      alignItems: "center",
      gap: 10,
    },
    logo: {
      width: 44,
      height: 44,
      borderRadius: 12,
      background: "linear-gradient(135deg,#6366f1,#22c55e)",
      display: "grid",
      placeItems: "center",
      color: "white",
      fontWeight: 800,
      letterSpacing: 0.5,
      boxShadow: "0 10px 20px rgba(99,102,241,0.18)",
    },
    titleWrap: { lineHeight: 1.1 },
    title: { fontSize: 22, margin: 0, fontWeight: 800 },
    subtitle: { margin: 0, marginTop: 4, color: "#6b7280", fontSize: 13 },

    actions: {
      display: "flex",
      gap: 10,
      flexWrap: "wrap",
      justifyContent: "flex-end",
    },
    btn: {
      border: "1px solid #e5e7eb",
      background: "white",
      borderRadius: 10,
      padding: "10px 12px",
      fontWeight: 700,
      cursor: "pointer",
      boxShadow: "0 6px 16px rgba(17,24,39,0.06)",
      transition: "transform .08s ease, box-shadow .12s ease",
    },
    btnPrimary: {
      background: "linear-gradient(135deg,#6366f1,#8b5cf6)",
      border: "none",
      color: "white",
    },
    btnOutline: {
      background: "white",
      border: "1px solid #c7d2fe",
      color: "#4338ca",
    },
    btnDanger: {
      background: "linear-gradient(135deg,#ef4444,#f97316)",
      border: "none",
      color: "white",
    },
    btnDisabled: {
      opacity: 0.55,
      cursor: "not-allowed",
    },

    grid: {
      display: "grid",
      gridTemplateColumns: "2fr 1fr",
      gap: 16,
      alignItems: "start",
      marginTop: 12,
    },

    card: {
      background: "white",
      border: "1px solid #eef2ff",
      borderRadius: 16,
      padding: 16,
      boxShadow: "0 14px 30px rgba(17,24,39,0.06)",
    },

    itemsHeader: {
      display: "flex",
      alignItems: "baseline",
      justifyContent: "space-between",
      gap: 10,
      marginBottom: 12,
    },
    h2: { margin: 0, fontSize: 18, fontWeight: 800 },
    hint: { margin: 0, color: "#6b7280", fontSize: 13 },

    itemGrid: {
      display: "grid",
      gridTemplateColumns: "1fr 1fr",
      gap: 12,
    },
    itemCard: {
      borderRadius: 14,
      padding: 14,
      border: "1px solid #e5e7eb",
      cursor: "pointer",
      display: "flex",
      gap: 12,
      alignItems: "center",
      transition: "transform .08s ease, box-shadow .12s ease",
      userSelect: "none",
    },
    emoji: {
      width: 44,
      height: 44,
      borderRadius: 12,
      display: "grid",
      placeItems: "center",
      fontSize: 24,
      boxShadow: "0 10px 20px rgba(17,24,39,0.08)",
      border: "1px solid rgba(229,231,235,0.8)",
      background: "#fff",
      flex: "0 0 44px",
    },
    itemName: { margin: 0, fontWeight: 800, fontSize: 16 },
    itemMeta: { margin: 0, color: "#6b7280", fontSize: 12, marginTop: 3 },

    cartRow: {
      display: "flex",
      alignItems: "center",
      justifyContent: "space-between",
      gap: 10,
      padding: "10px 0",
      borderBottom: "1px solid #f3f4f6",
    },
    cartLeft: { display: "flex", alignItems: "center", gap: 10 },
    pill: {
      fontSize: 12,
      fontWeight: 800,
      padding: "4px 10px",
      borderRadius: 999,
      background: "#eef2ff",
      color: "#4338ca",
      border: "1px solid #c7d2fe",
    },
    removeBtn: {
      borderRadius: 10,
      padding: "8px 10px",
      border: "1px solid #fecaca",
      background: "#fff5f5",
      color: "#b91c1c",
      fontWeight: 800,
      cursor: "pointer",
    },
    empty: { color: "#6b7280", fontSize: 13, marginTop: 8 },
  };

  const btnStyle = (base, extra) => ({
    ...styles.btn,
    ...extra,
    ...(loading ? styles.btnDisabled : {}),
    ...base,
  });

  return (
    <div style={styles.page}>
      <div style={styles.shell}>
        {/* Header */}
        <div style={styles.header}>
          <div style={styles.brand}>
            <div style={styles.logo}>SC</div>
            <div style={styles.titleWrap}>
              <p style={styles.title}>Shopping Cart</p>
              <p style={styles.subtitle}>Pick items, manage cart, and checkout</p>
            </div>
          </div>

          <div style={styles.actions}>
            <button
              onClick={checkout}
              disabled={loading}
              style={btnStyle(styles.btn, styles.btnPrimary)}
            >
              âœ… Checkout
            </button>
            <button
              onClick={showCart}
              disabled={loading}
              style={btnStyle(styles.btn, styles.btnOutline)}
            >
              ðŸ§¾ Cart
            </button>
            <button
              onClick={showOrderHistory}
              disabled={loading}
              style={btnStyle(styles.btn, {})}
            >
              ðŸ“¦ Order History
            </button>
            <button
              onClick={onLogout}
              style={{ ...styles.btn, ...styles.btnDanger }}
            >
              ðŸšª Logout
            </button>
          </div>
        </div>

        {/* Main Grid */}
        <div style={styles.grid}>
          {/* Items */}
          <div style={styles.card}>
            <div style={styles.itemsHeader}>
              <div>
                <p style={styles.h2}>Items</p>
                <p style={styles.hint}>Click any item to add it to cart.</p>
              </div>
              <div style={styles.pill}>{items.length} items</div>
            </div>

            <div style={styles.itemGrid}>
              {items.map((it) => (
                <div
                  key={it.id}
                  onClick={() => addToCart(it.id)}
                  style={{
                    ...styles.itemCard,
                    background: getItemGradient(it.name),
                    opacity: loading ? 0.7 : 1,
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.transform = "translateY(-2px)";
                    e.currentTarget.style.boxShadow = "0 14px 30px rgba(17,24,39,0.08)";
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.transform = "translateY(0px)";
                    e.currentTarget.style.boxShadow = "none";
                  }}
                >
                  <div style={styles.emoji}>{getItemEmoji(it.name)}</div>
                  <div>
                    <p style={styles.itemName}>{it.name}</p>
                    <p style={styles.itemMeta}>Item ID: {it.id}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Cart */}
          <div style={styles.card}>
            <div style={styles.itemsHeader}>
              <div>
                <p style={styles.h2}>Cart Items</p>
                <p style={styles.hint}>Remove items if needed.</p>
              </div>
              <div style={styles.pill}>{cart?.cart_items?.length || 0} in cart</div>
            </div>

            {!cart?.cart_items?.length ? (
              <div style={styles.empty}>Your cart is empty.</div>
            ) : (
              <div>
                {cart.cart_items.map((ci, idx) => {
                  const name = itemNameById.get(ci.item_id) || "Unknown";
                  return (
                    <div key={ci.id || idx} style={styles.cartRow}>
                      <div style={styles.cartLeft}>
                        <div style={styles.emoji}>{getItemEmoji(name)}</div>
                        <div>
                          <div style={{ fontWeight: 900 }}>{name}</div>
                          <div style={{ fontSize: 12, color: "#6b7280" }}>
                            Item #{ci.item_id}
                          </div>
                        </div>
                      </div>

                      <button
                        onClick={() => removeFromCart(ci.item_id)}
                        disabled={loading}
                        style={{
                          ...styles.removeBtn,
                          ...(loading ? styles.btnDisabled : {}),
                        }}
                      >
                        Remove
                      </button>
                    </div>
                  );
                })}
              </div>
            )}

            <div style={{ marginTop: 12, color: "#6b7280", fontSize: 12 }}>
              Tip: Use <b>Cart</b> button to see (cart_id, item_id) pairs.
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
