import React, { useEffect, useState } from "react";
import { api } from "./api";

export default function ItemsPage({ onLogout }) {
  const [items, setItems] = useState([]);
  const [cart, setCart] = useState(null); // response from POST /carts
  const [loading, setLoading] = useState(false);

  const loadItems = async () => {
    const res = await api.get("/items");
    setItems(res.data);
  };

  useEffect(() => {
    loadItems();
  }, []);

  const addToCart = async (itemId) => {
    try {
      setLoading(true);
      const res = await api.post("/carts", { itemId });
      setCart(res.data);
    } catch (e) {
      window.alert("Failed to add item to cart. Please login again.");
    } finally {
      setLoading(false);
    }
  };

  
  const showOrderHistory = async () => {
    try {
      const res = await api.get("/orders");
      const orders = res.data || [];
      const ids = orders.map((o) => o.id).join(", ");
      window.alert(ids ? `Order IDs: ${ids}` : "No orders yet.");
    } catch (e) {
      window.alert("Failed to fetch orders.");
    }
  };

  const checkout = async () => {
    if (!cart?.cart?.id) {
      window.alert("Cart is empty.");
      return;
    }
    try {
      await api.post("/orders", { cartId: cart.cart.id });
      window.alert("Order successful");
      setCart(null); // clear local cart after order
    } catch (e) {
      window.alert("Checkout failed. Make sure cart has items.");
    }
  };

  return (
    <div style={{ maxWidth: 800, margin: "40px auto", fontFamily: "Arial" }}>
      <div style={{ display: "flex", gap: 10, marginBottom: 16 }}>
        <button onClick={checkout} disabled={loading}>
          Checkout
        </button>
        <button onClick={showCart} disabled={loading}>
          Cart
        </button>
        <button onClick={showOrderHistory} disabled={loading}>
          Order History
        </button>
        <button onClick={onLogout} style={{ marginLeft: "auto" }}>
          Logout
        </button>
      </div>

      <h2>Items</h2>
      <div style={{ color: "#666", marginBottom: 10 }}>
        Click any item to add it to cart.
      </div>

      <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 12 }}>
        {items.map((it) => (
          <div
            key={it.id}
            onClick={() => addToCart(it.id)}
            style={{
              border: "1px solid #ddd",
              borderRadius: 8,
              padding: 12,
              cursor: "pointer",
              opacity: loading ? 0.7 : 1,
            }}
          >
            <div style={{ fontWeight: "bold" }}>{it.name}</div>
            <div style={{ fontSize: 12, color: "#666" }}>Item ID: {it.id}</div>
          </div>
        ))}
      </div>
    </div>
  );
}
