import React, { useState } from "react";
import { api } from "./api";

export default function LoginPage({ onLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);

  const submit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const res = await api.post("/users/login", {
        username: username.trim(),
        password: password.trim(),
      });
      onLogin(res.data.token);
    } catch (err) {
      window.alert("Invalid username/password.");
    } finally {
      setLoading(false);
    }
  };

  const styles = {
    page: {
      minHeight: "100vh",
      display: "grid",
      placeItems: "center",
      padding: 16,
      fontFamily: "Inter, Arial, sans-serif",
      background: "linear-gradient(180deg,#f6f8ff,#ffffff)",
    },
    card: {
      width: "100%",
      maxWidth: 420,
      background: "white",
      border: "1px solid #eef2ff",
      borderRadius: 18,
      padding: 18,
      boxShadow: "0 18px 40px rgba(17,24,39,0.08)",
    },
    brand: {
      display: "flex",
      alignItems: "center",
      gap: 10,
      marginBottom: 14,
    },
    logo: {
      width: 44,
      height: 44,
      borderRadius: 12,
      background: "linear-gradient(135deg,#6366f1,#22c55e)",
      display: "grid",
      placeItems: "center",
      color: "white",
      fontWeight: 800,
      letterSpacing: 0.5,
      boxShadow: "0 10px 20px rgba(99,102,241,0.18)",
    },
    title: { margin: 0, fontSize: 20, fontWeight: 900, color: "#111827" },
    subtitle: { margin: "4px 0 0 0", color: "#6b7280", fontSize: 13 },

    label: { fontSize: 12, fontWeight: 800, color: "#374151", marginBottom: 6 },
    input: {
      width: "100%",
      padding: "12px 12px",
      borderRadius: 12,
      border: "1px solid #e5e7eb",
      outline: "none",
      fontSize: 14,
      background: "#fff",
      boxShadow: "0 6px 16px rgba(17,24,39,0.04)",
    },
    inputWrap: { marginBottom: 12 },
    hintRow: {
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      marginTop: 6,
      color: "#6b7280",
      fontSize: 12,
    },
    btn: {
      width: "100%",
      border: "none",
      borderRadius: 12,
      padding: "12px 12px",
      fontWeight: 900,
      cursor: "pointer",
      color: "white",
      background: "linear-gradient(135deg,#6366f1,#8b5cf6)",
      boxShadow: "0 14px 30px rgba(99,102,241,0.20)",
      transition: "transform .08s ease, box-shadow .12s ease",
      marginTop: 6,
    },
    btnDisabled: { opacity: 0.65, cursor: "not-allowed" },
    note: {
      marginTop: 12,
      fontSize: 12,
      color: "#6b7280",
      background: "#f9fafb",
      border: "1px solid #e5e7eb",
      padding: 10,
      borderRadius: 12,
      lineHeight: 1.4,
    },
    code: {
      fontFamily: "ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, monospace",
      fontSize: 12,
      background: "#eef2ff",
      border: "1px solid #c7d2fe",
      color: "#4338ca",
      padding: "2px 6px",
      borderRadius: 8,
    },
  };

  const canSubmit = username.trim() && password.trim() && !loading;

  return (
    <div style={styles.page}>
      <div style={styles.card}>
        <div style={styles.brand}>
          <div style={styles.logo}>SC</div>
          <div>
            <p style={styles.title}>Welcome back</p>
            <p style={styles.subtitle}>Login to continue to your cart</p>
          </div>
        </div>

        <form onSubmit={submit}>
          <div style={styles.inputWrap}>
            <div style={styles.label}>Username</div>
            <input
              placeholder="Enter username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              style={styles.input}
            />
          </div>

          <div style={styles.inputWrap}>
            <div style={styles.label}>Password</div>
            <input
              placeholder="Enter password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={styles.input}
            />
            <div style={styles.hintRow}>
              <span>Tip: no trailing spaces</span>
              <span>ðŸ”’ Secure login</span>
            </div>
          </div>

          <button
            style={{ ...styles.btn, ...(canSubmit ? {} : styles.btnDisabled) }}
            disabled={!canSubmit}
            onMouseEnter={(e) => {
              if (!canSubmit) return;
              e.currentTarget.style.transform = "translateY(-1px)";
              e.currentTarget.style.boxShadow = "0 18px 40px rgba(99,102,241,0.24)";
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.transform = "translateY(0px)";
              e.currentTarget.style.boxShadow = "0 14px 30px rgba(99,102,241,0.20)";
            }}
          >
            {loading ? "Logging in..." : "Login"}
          </button>
        </form>

        <div style={styles.note}>
          Signup is done via backend endpoint <span style={styles.code}>POST /users</span>.
          <br />
          If you want, I can also add a Signup screen in React.
        </div>
      </div>
    </div>
  );
}
