import React, { useEffect, useMemo, useState } from "react";
import { api } from "./api";

export default function ItemsPage({ onLogout }) {
  const [items, setItems] = useState([]);
  const [cart, setCart] = useState(null);

  const [orders, setOrders] = useState([]);
  const [expandedOrderId, setExpandedOrderId] = useState(null);
  const [orderItemsById, setOrderItemsById] = useState({});

  const [loading, setLoading] = useState(false);

  // ---------------------------
  // Data loaders
  // ---------------------------
  const loadItems = async () => {
    const res = await api.get("/items");
    setItems(res.data || []);
  };

  // âœ… Fix cart not showing after re-login:
  // Requires backend to support GET /carts/me
  const loadMyCart = async () => {
    try {
      const res = await api.get("/carts/me");
      setCart(res.data);
    } catch {
      setCart(null);
    }
  };

  const loadOrders = async () => {
    try {
      const res = await api.get("/orders");
      setOrders(res.data || []);
    } catch {
      setOrders([]);
    }
  };

  useEffect(() => {
    loadItems();
    loadMyCart();
    loadOrders();
  }, []);

  // ---------------------------
  // Helpers
  // ---------------------------
  const itemNameById = useMemo(
    () => new Map(items.map((i) => [i.id, i.name])),
    [items]
  );

  const getEmoji = (name) => {
    const n = (name || "").toLowerCase();
    if (n.includes("apple")) return "ðŸŽ";
    if (n.includes("milk")) return "ðŸ¥›";
    if (n.includes("bread")) return "ðŸž";
    if (n.includes("egg")) return "ðŸ¥š";
    if (n.includes("rice")) return "ðŸš";
    return "ðŸ›’";
  };

  const fmtDate = (iso) => {
    if (!iso) return "â€”";
    try {
      const d = new Date(iso);
      return d.toLocaleString();
    } catch {
      return iso;
    }
  };

  // ---------------------------
  // Actions
  // ---------------------------
  const addToCart = async (itemId) => {
    try {
      setLoading(true);
      const res = await api.post("/carts", { itemId });
      setCart(res.data);
    } catch (e) {
      console.error(e);
      window.alert("Failed to add item. Please login again.");
    } finally {
      setLoading(false);
    }
  };

  const removeFromCart = async (itemId) => {
    try {
      setLoading(true);

      // NOTE: If your axios instance already has Authorization header
      // (via setAuthToken), you can remove this token part.
      const token = localStorage.getItem("token");

      const res = await api.delete(`/carts/items/${itemId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setCart(res.data);
    } catch (e) {
      console.error(e);
      const msg = e?.response?.data?.error || e?.response?.data || e.message;
      window.alert(msg);
    } finally {
      setLoading(false);
    }
  };

  const checkout = async () => {
    if (!cart?.cart?.id) {
      window.alert("Cart is empty.");
      return;
    }
    try {
      setLoading(true);
      await api.post("/orders", { cartId: cart.cart.id });
      window.alert("Order placed âœ…");

      // clear local UI cart
      setCart(null);

      // refresh cart + orders
      await loadMyCart();
      await loadOrders();
    } catch (e) {
      console.error(e);
      const msg = e?.response?.data?.error || e?.response?.data || e.message;
      window.alert(msg);
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    setCart(null);
    setOrders([]);
    setExpandedOrderId(null);
    setOrderItemsById({});
    onLogout();
  };

  // ---------------------------
  // Order expand + load items
  // ---------------------------
  const loadOrderItems = async (orderId) => {
    // already loaded
    if (orderItemsById[orderId]) return;

    // Recommended backend endpoint:
    // GET /orders/:id => { order: {...}, items: [...] } OR { items: [...] } OR just [...]
    const res = await api.get(`/orders/${orderId}`);
    const items = res.data?.items || res.data || [];
    setOrderItemsById((prev) => ({ ...prev, [orderId]: items }));
  };

  const onOrderClick = async (orderId) => {
    if (expandedOrderId === orderId) {
      setExpandedOrderId(null);
      return;
    }

    setExpandedOrderId(orderId);

    try {
      await loadOrderItems(orderId);
    } catch (e) {
      console.error(e);
      const msg =
        e?.response?.data?.error ||
        "Backend does not provide order items. Add GET /orders/:id.";
      window.alert(msg);
    }
  };

  // ---------------------------
  // Styles (responsive)
  // ---------------------------
  const styles = {
    page: {
      minHeight: "100vh",
      background: "linear-gradient(180deg,#f6f8ff,#ffffff)",
      fontFamily: "Inter, Arial, sans-serif",
      padding: 16,
    },
    wrap: { maxWidth: 1100, margin: "0 auto" },
    headerBar: {
      display: "flex",
      alignItems: "center",
      justifyContent: "space-between",
      gap: 12,
      flexWrap: "wrap",
      marginBottom: 16,
    },
    brand: { display: "flex", alignItems: "center", gap: 10 },
    logo: {
      width: 44,
      height: 44,
      borderRadius: 12,
      background: "linear-gradient(135deg,#6366f1,#22c55e)",
      display: "grid",
      placeItems: "center",
      color: "#fff",
      fontWeight: 900,
      flex: "0 0 44px",
    },
    title: { margin: 0, fontSize: 22, fontWeight: 900, color: "#111827" },
    sub: { margin: "4px 0 0", color: "#6b7280", fontSize: 13 },

    actionsRow: { display: "flex", gap: 10, flexWrap: "wrap" },
    btn: {
      border: "1px solid #e5e7eb",
      background: "#fff",
      borderRadius: 12,
      padding: "10px 12px",
      fontWeight: 900,
      cursor: "pointer",
      boxShadow: "0 6px 16px rgba(17,24,39,0.06)",
      whiteSpace: "nowrap",
    },
    btnPrimary: {
      background: "linear-gradient(135deg,#6366f1,#8b5cf6)",
      color: "#fff",
      border: "none",
    },
    btnDanger: {
      background: "linear-gradient(135deg,#ef4444,#f97316)",
      color: "#fff",
      border: "none",
    },
    disabled: { opacity: 0.6, cursor: "not-allowed" },

    grid: {
      display: "grid",
      gridTemplateColumns: "1.2fr 0.8fr",
      gap: 14,
    },

    card: {
      background: "#fff",
      border: "1px solid #eef2ff",
      borderRadius: 18,
      boxShadow: "0 12px 26px rgba(17,24,39,0.08)",
      padding: 16,
    },

    sectionTitle: { margin: 0, fontSize: 18, fontWeight: 900, color: "#111827" },
    hint: { margin: "6px 0 0", color: "#6b7280", fontSize: 13 },

    badge: {
      fontSize: 12,
      fontWeight: 900,
      color: "#4338ca",
      background: "#eef2ff",
      padding: "4px 10px",
      borderRadius: 999,
      whiteSpace: "nowrap",
    },

    itemsGrid: {
      marginTop: 12,
      display: "grid",
      gridTemplateColumns: "repeat(2, minmax(0, 1fr))",
      gap: 12,
    },

    itemCard: {
      display: "flex",
      alignItems: "center",
      gap: 12,
      border: "1px solid #e5e7eb",
      borderRadius: 14,
      padding: 12,
      cursor: "pointer",
      background: "#fff",
    },

    icon: {
      width: 44,
      height: 44,
      borderRadius: 12,
      border: "1px solid #e5e7eb",
      display: "grid",
      placeItems: "center",
      fontSize: 24,
      background: "#f9fafb",
      flex: "0 0 44px",
    },

    cartRow: {
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      gap: 10,
      padding: "10px 0",
      borderBottom: "1px solid #f3f4f6",
    },

    removeBtn: {
      borderRadius: 10,
      padding: "8px 10px",
      border: "1px solid #fecaca",
      background: "#fff5f5",
      color: "#b91c1c",
      fontWeight: 900,
      cursor: "pointer",
      whiteSpace: "nowrap",
    },

    // order styles
    orderRow: {
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      gap: 12,
      padding: "12px 0",
      borderBottom: "1px solid #f3f4f6",
    },
    orderLeft: { display: "grid", gap: 2 },
    orderTitle: { fontWeight: 900, color: "#111827" },
    orderMeta: { fontSize: 12, color: "#6b7280" },
    status: {
      fontSize: 12,
      fontWeight: 900,
      color: "#075985",
      background: "#e0f2fe",
      border: "1px solid #bae6fd",
      padding: "6px 10px",
      borderRadius: 999,
      whiteSpace: "nowrap",
    },
    orderItemsWrap: {
      padding: "10px 0 14px 0",
      display: "grid",
      gap: 8,
    },
    orderItemCard: {
      display: "flex",
      alignItems: "center",
      gap: 10,
      border: "1px solid #f3f4f6",
      borderRadius: 12,
      padding: 10,
      background: "#fafafa",
    },
  };

  return (
    <div style={styles.page}>
      {/* responsive CSS */}
      <style>{`
        @media (max-width: 900px) {
          .grid { grid-template-columns: 1fr !important; }
          .itemsGrid { grid-template-columns: 1fr !important; }
          .actionsRow { width: 100% !important; }
        }
      `}</style>

      <div style={styles.wrap}>
        {/* Header */}
        <div style={styles.headerBar}>
          <div style={styles.brand}>
            <div style={styles.logo}>SC</div>
            <div>
              <p style={styles.title}>Shopping Cart</p>
              <p style={styles.sub}>Add items, view cart, and checkout</p>
            </div>
          </div>

          <div className="actionsRow" style={styles.actionsRow}>
            <button
              onClick={checkout}
              disabled={loading}
              style={{
                ...styles.btn,
                ...styles.btnPrimary,
                ...(loading ? styles.disabled : {}),
              }}
            >
              âœ… Checkout
            </button>

            <button
              onClick={loadOrders}
              disabled={loading}
              style={{ ...styles.btn, ...(loading ? styles.disabled : {}) }}
            >
              ðŸ“¦ Refresh Orders
            </button>

            <button onClick={handleLogout} style={{ ...styles.btn, ...styles.btnDanger }}>
              ðŸšª Logout
            </button>
          </div>
        </div>

        {/* Main grid */}
        <div className="grid" style={styles.grid}>
          {/* Items */}
          <div style={styles.card}>
            <div style={{ display: "flex", justifyContent: "space-between", alignItems: "baseline", gap: 10 }}>
              <div>
                <p style={styles.sectionTitle}>Items</p>
                <p style={styles.hint}>Tap an item to add to cart.</p>
              </div>
              <span style={styles.badge}>{items.length} items</span>
            </div>

            <div className="itemsGrid" style={styles.itemsGrid}>
              {items.map((it) => (
                <div
                  key={it.id}
                  onClick={() => addToCart(it.id)}
                  style={{ ...styles.itemCard, opacity: loading ? 0.75 : 1 }}
                >
                  <div style={styles.icon}>{getEmoji(it.name)}</div>
                  <div>
                    <div style={{ fontWeight: 900, color: "#111827" }}>{it.name}</div>
                    <div style={{ fontSize: 12, color: "#6b7280" }}>Item ID: {it.id}</div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Right panel: Cart + Orders */}
          <div style={{ display: "grid", gap: 14 }}>
            {/* Cart */}
            <div style={styles.card}>
              <div style={{ display: "flex", justifyContent: "space-between", alignItems: "baseline", gap: 10 }}>
                <div>
                  <p style={styles.sectionTitle}>Cart</p>
                  <p style={styles.hint}>Remove items if needed.</p>
                </div>
                <span style={styles.badge}>{cart?.cart_items?.length || 0} in cart</span>
              </div>

              {!cart?.cart_items?.length ? (
                <div style={{ marginTop: 12, color: "#6b7280", fontSize: 13 }}>Your cart is empty.</div>
              ) : (
                <div style={{ marginTop: 12 }}>
                  {cart.cart_items.map((ci, idx) => {
                    const name = itemNameById.get(ci.item_id) || "Unknown";
                    return (
                      <div key={ci.id || idx} style={styles.cartRow}>
                        <div style={{ display: "flex", alignItems: "center", gap: 10 }}>
                          <div style={styles.icon}>{getEmoji(name)}</div>
                          <div>
                            <div style={{ fontWeight: 900 }}>{name}</div>
                            <div style={{ fontSize: 12, color: "#6b7280" }}>Item #{ci.item_id}</div>
                          </div>
                        </div>

                        <button
                          onClick={() => removeFromCart(ci.item_id)}
                          disabled={loading}
                          style={{ ...styles.removeBtn, ...(loading ? styles.disabled : {}) }}
                        >
                          Remove
                        </button>
                      </div>
                    );
                  })}
                </div>
              )}
            </div>

            {/* Orders */}
            <div style={styles.card}>
              <div style={{ display: "flex", justifyContent: "space-between", alignItems: "baseline", gap: 10 }}>
                <div>
                  <p style={styles.sectionTitle}>Order History</p>
                  <p style={styles.hint}>Click an order to view items.</p>
                </div>
                <span style={styles.badge}>{orders.length} orders</span>
              </div>

              {!orders.length ? (
                <div style={{ marginTop: 12, color: "#6b7280", fontSize: 13 }}>No orders yet.</div>
              ) : (
                <div style={{ marginTop: 12 }}>
                  {orders
                    .slice()
                    .sort((a, b) => (b.id || 0) - (a.id || 0))
                    .map((o) => (
                      <div key={o.id}>
                        <div
                          style={{ ...styles.orderRow, cursor: "pointer" }}
                          onClick={() => onOrderClick(o.id)}
                          title="Click to expand"
                        >
                          <div style={styles.orderLeft}>
                            <div style={styles.orderTitle}>Order #{o.id}</div>
                            <div style={styles.orderMeta}>Date: {fmtDate(o.created_at)}</div>
                            <div style={styles.orderMeta}>Cart ID: {o.cart_id ?? "â€”"}</div>
                          </div>

                          <div style={{ display: "flex", alignItems: "center", gap: 10 }}>
                            <span style={styles.status}>PLACED</span>
                            <span style={{ fontSize: 18 }}>{expandedOrderId === o.id ? "â–¾" : "â–¸"}</span>
                          </div>
                        </div>

                        {expandedOrderId === o.id && (
                          <div style={styles.orderItemsWrap}>
                            {!orderItemsById[o.id] ? (
                              <div style={{ fontSize: 13, color: "#6b7280" }}>Loading items...</div>
                            ) : orderItemsById[o.id].length === 0 ? (
                              <div style={{ fontSize: 13, color: "#6b7280" }}>
                                No item data returned for this order.
                              </div>
                            ) : (
                              orderItemsById[o.id].map((it, idx) => {
                                const name = it.name || itemNameById.get(it.item_id) || `Item #${it.item_id}`;
                                const itemId = it.item_id ?? it.id ?? "â€”";
                                return (
                                  <div
                                    key={it.id || `${itemId}-${idx}`}
                                    style={styles.orderItemCard}
                                  >
                                    <div style={styles.icon}>{getEmoji(name)}</div>
                                    <div>
                                      <div style={{ fontWeight: 900 }}>{name}</div>
                                      <div style={{ fontSize: 12, color: "#6b7280" }}>
                                        Item ID: {itemId}
                                      </div>
                                    </div>
                                  </div>
                                );
                              })
                            )}
                          </div>
                        )}
                      </div>
                    ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
