import React, { useEffect, useState } from "react";
import { api } from "./api";

export default function ItemsPage({ onLogout }) {
  const [items, setItems] = useState([]);
  const [cart, setCart] = useState(null); // response from POST /carts or DELETE cart item
  const [loading, setLoading] = useState(false);

  const loadItems = async () => {
    const res = await api.get("/items");
    setItems(res.data);
  };

  useEffect(() => {
    loadItems();
  }, []);

  const addToCart = async (itemId) => {
    try {
      setLoading(true);
      const res = await api.post("/carts", { itemId });
      setCart(res.data);
    } catch (e) {
      window.alert("Failed to add item to cart. Please login again.");
    } finally {
      setLoading(false);
    }
  };

  // ✅ NEW: remove from cart (requires backend DELETE /carts/items/:itemId)
  const removeFromCart = async (itemId) => {
    try {
      setLoading(true);
      const res = await api.delete(`/carts/items/${itemId}`);
      setCart(res.data);
    } catch {
      window.alert("Item not found in cart.");
    } finally {
      setLoading(false);
    }
  };

  // ✅ Updated: show cart with item names also
  const showCart = () => {
    if (!cart?.cart_items?.length) {
      window.alert("Cart is empty.");
      return;
    }

    const itemNameById = new Map(items.map((i) => [i.id, i.name]));

    const lines = cart.cart_items.map((ci) => {
      const name = itemNameById.get(ci.item_id) || "Unknown";
      return `(${ci.cart_id}, ${ci.item_id})  -  ${name}`;
    });

    window.alert(lines.join("\n"));
  };

  const showOrderHistory = async () => {
    try {
      const res = await api.get("/orders");
      const orders = res.data || [];
      const ids = orders.map((o) => o.id).join(", ");
      window.alert(ids ? `Order IDs: ${ids}` : "No orders yet.");
    } catch (e) {
      window.alert("Failed to fetch orders.");
    }
  };

  const checkout = async () => {
    if (!cart?.cart?.id) {
      window.alert("Cart is empty.");
      return;
    }
    try {
      setLoading(true);
      await api.post("/orders", { cartId: cart.cart.id });
      window.alert("Order successful");
      setCart(null); // clear local cart after order
    } catch (e) {
      window.alert("Checkout failed. Make sure cart has items.");
    } finally {
      setLoading(false);
    }
  };

  // For showing item names in Cart Items section
  const itemNameById = new Map(items.map((i) => [i.id, i.name]));

  return (
    <div style={{ maxWidth: 800, margin: "40px auto", fontFamily: "Arial" }}>
      <div style={{ display: "flex", gap: 10, marginBottom: 16 }}>
        <button onClick={checkout} disabled={loading}>
          Checkout
        </button>
        <button onClick={showCart} disabled={loading}>
          Cart
        </button>
        <button onClick={showOrderHistory} disabled={loading}>
          Order History
        </button>
        <button onClick={onLogout} style={{ marginLeft: "auto" }}>
          Logout
        </button>
      </div>

      <h2>Items</h2>
      <div style={{ color: "#666", marginBottom: 10 }}>
        Click any item to add it to cart.
      </div>

      <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 12 }}>
        {items.map((it) => (
          <div
            key={it.id}
            onClick={() => addToCart(it.id)}
            style={{
              border: "1px solid #ddd",
              borderRadius: 8,
              padding: 12,
              cursor: "pointer",
              opacity: loading ? 0.7 : 1,
            }}
          >
            <div style={{ fontWeight: "bold" }}>{it.name}</div>
            <div style={{ fontSize: 12, color: "#666" }}>Item ID: {it.id}</div>
          </div>
        ))}
      </div>

      {/* ✅ NEW: visible cart items section */}
      <h3 style={{ marginTop: 24 }}>Cart Items</h3>
      {!cart?.cart_items?.length ? (
        <div style={{ color: "#666" }}>Cart is empty</div>
      ) : (
        <ul>
          {cart.cart_items.map((ci, idx) => (
            <li key={ci.id || idx} style={{ marginBottom: 6 }}>
              <span>
                Item #{ci.item_id} - {itemNameById.get(ci.item_id) || "Unknown"}
              </span>{" "}
              <button onClick={() => removeFromCart(ci.item_id)} disabled={loading}>
                Remove
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
