import React, { useEffect, useMemo, useState } from "react";
import { api } from "./api";

export default function ItemsPage({ onLogout }) {
  const [items, setItems] = useState([]);
  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(false);

  const loadItems = async () => {
    const res = await api.get("/items");
    setItems(res.data);
  };

  useEffect(() => {
    loadItems();
  }, []);

  const addToCart = async (itemId) => {
    try {
      setLoading(true);
      const res = await api.post("/carts", { itemId });
      setCart(res.data);
    } catch (e) {
      console.error(e);
      window.alert("Failed to add item to cart. Please login again.");
    } finally {
      setLoading(false);
    }
  };

  const removeFromCart = async (itemId) => {
    try {
      setLoading(true);
      const token = localStorage.getItem("token");

      const res = await api.delete(`/carts/items/${itemId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setCart(res.data);
    } catch (e) {
      console.error(e);
      const msg = e?.response?.data?.error || e?.response?.data || e.message;
      window.alert(msg);
    } finally {
      setLoading(false);
    }
  };

  const showCart = () => {
    if (!cart?.cart_items?.length) {
      window.alert("Cart is empty.");
      return;
    }
    const itemNameById = new Map(items.map((i) => [i.id, i.name]));
    const lines = cart.cart_items.map((ci) => {
      const name = itemNameById.get(ci.item_id) || "Unknown";
      return `(${ci.cart_id}, ${ci.item_id}) - ${name}`;
    });
    window.alert(lines.join("\n"));
  };

  const showOrderHistory = async () => {
    try {
      const res = await api.get("/orders");
      const orders = res.data || [];
      const ids = orders.map((o) => o.id).join(", ");
      window.alert(ids ? `Order IDs: ${ids}` : "No orders yet.");
    } catch (e) {
      console.error(e);
      window.alert("Failed to fetch orders.");
    }
  };

  const checkout = async () => {
    if (!cart?.cart?.id) {
      window.alert("Cart is empty.");
      return;
    }
    try {
      setLoading(true);
      await api.post("/orders", { cartId: cart.cart.id });
      window.alert("Order successful");
      setCart(null);
    } catch (e) {
      console.error(e);
      const msg = e?.response?.data?.error || e?.response?.data || e.message;
      window.alert(msg);
    } finally {
      setLoading(false);
    }
  };

  const itemNameById = useMemo(() => new Map(items.map((i) => [i.id, i.name])), [items]);

  // Emoji "images" (works everywhere without assets)
  const getItemEmoji = (name) => {
    const n = (name || "").toLowerCase();
    if (n.includes("apple")) return "ðŸŽ";
    if (n.includes("milk")) return "ðŸ¥›";
    if (n.includes("bread")) return "ðŸž";
    if (n.includes("egg")) return "ðŸ¥š";
    if (n.includes("rice")) return "ðŸš";
    return "ðŸ›’";
  };

  const getItemGradient = (name) => {
    const n = (name || "").toLowerCase();
    if (n.includes("apple")) return "linear-gradient(135deg,#ffe3e3,#fff)";
    if (n.includes("milk")) return "linear-gradient(135deg,#e6f4ff,#fff)";
    if (n.includes("bread")) return "linear-gradient(135deg,#fff1da,#fff)";
    if (n.includes("egg")) return "linear-gradient(135deg,#fff7d6,#fff)";
    if (n.includes("rice")) return "linear-gradient(135deg,#e9ffe9,#fff)";
    return "linear-gradient(135deg,#f1f5ff,#fff)";
  };

  const styles = {
    page: {
      minHeight: "100vh",
      background: "linear-gradient(180deg,#f6f8ff,#ffffff)",
      fontFamily: "Inter, Arial, sans-serif",
      color: "#111827",
      padding: 0,
    },
    header: {
      display: "flex",
      alignItems: "center",
      justifyContent: "space-between",
      gap: 12,
      marginBottom: 16,
      flexWrap: "wrap",
    },
    brand: { display: "flex", alignItems: "center", gap: 10 },
    logo: {
      width: 44,
      height: 44,
      borderRadius: 12,
      background: "linear-gradient(135deg,#6366f1,#22c55e)",
      display: "grid",
      placeItems: "center",
      color: "white",
      fontWeight: 900,
      boxShadow: "0 10px 20px rgba(99,102,241,0.18)",
    },
    title: { margin: 0, fontSize: 22, fontWeight: 900 },
    subtitle: { margin: "4px 0 0 0", color: "#6b7280", fontSize: 13 },

    btn: {
      border: "1px solid #e5e7eb",
      background: "white",
      borderRadius: 12,
      padding: "10px 12px",
      fontWeight: 900,
      cursor: "pointer",
      boxShadow: "0 6px 16px rgba(17,24,39,0.06)",
    },
    btnPrimary: {
      background: "linear-gradient(135deg,#6366f1,#8b5cf6)",
      border: "none",
      color: "white",
    },
    btnOutline: { border: "1px solid #c7d2fe", color: "#4338ca" },
    btnDanger: { background: "linear-gradient(135deg,#ef4444,#f97316)", border: "none", color: "white" },
    disabled: { opacity: 0.6, cursor: "not-allowed" },

    cardPad: { padding: 16, borderRadius: 16 },

    sectionTitle: { margin: 0, fontSize: 18, fontWeight: 900 },
    hint: { margin: "6px 0 0 0", color: "#6b7280", fontSize: 13 },

    itemCard: {
      borderRadius: 14,
      padding: 14,
      border: "1px solid #e5e7eb",
      cursor: "pointer",
      display: "flex",
      gap: 12,
      alignItems: "center",
      userSelect: "none",
      transition: "transform .08s ease",
    },
    emojiBox: {
      width: 44,
      height: 44,
      borderRadius: 12,
      display: "grid",
      placeItems: "center",
      fontSize: 24,
      border: "1px solid rgba(229,231,235,0.9)",
      background: "white",
      boxShadow: "0 10px 20px rgba(17,24,39,0.08)",
      flex: "0 0 44px",
    },
    itemName: { margin: 0, fontWeight: 900, fontSize: 16 },
    itemMeta: { margin: "3px 0 0 0", color: "#6b7280", fontSize: 12 },

    cartRow: {
      display: "flex",
      alignItems: "center",
      justifyContent: "space-between",
      gap: 10,
      padding: "10px 0",
      borderBottom: "1px solid #f3f4f6",
    },
    removeBtn: {
      borderRadius: 10,
      padding: "8px 10px",
      border: "1px solid #fecaca",
      background: "#fff5f5",
      color: "#b91c1c",
      fontWeight: 900,
      cursor: "pointer",
    },
    badge: {
      fontSize: 12,
      fontWeight: 900,
      padding: "4px 10px",
      borderRadius: 999,
      background: "#eef2ff",
      color: "#4338ca",
      border: "1px solid #c7d2fe",
      display: "inline-block",
    },
    small: { color: "#6b7280", fontSize: 12, marginTop: 10 },
  };

  const btnMerged = (extra) => ({
    ...styles.btn,
    ...extra,
    ...(loading ? styles.disabled : {}),
  });

  return (
    <div style={styles.page}>
      <div className="container">
        {/* Header */}
        <div style={styles.header}>
          <div style={styles.brand}>
            <div style={styles.logo}>SC</div>
            <div>
              <p style={styles.title}>Shopping Cart</p>
              <p style={styles.subtitle}>Add items, view cart, and checkout</p>
            </div>
          </div>

          <div className="actionsRow" style={{ display: "flex", gap: 10, flexWrap: "wrap" }}>
            <button onClick={checkout} disabled={loading} style={btnMerged(styles.btnPrimary)}>
              âœ… Checkout
            </button>
            <button onClick={showCart} disabled={loading} style={btnMerged(styles.btnOutline)}>
              ðŸ§¾ Cart
            </button>
            <button onClick={showOrderHistory} disabled={loading} style={btnMerged({})}>
              ðŸ“¦ Order History
            </button>
            <button onClick={onLogout} style={{ ...styles.btn, ...styles.btnDanger }}>
              ðŸšª Logout
            </button>
          </div>
        </div>

        {/* Main */}
        <div className="mainGrid">
          {/* Items panel */}
          <div className="card" style={styles.cardPad}>
            <div style={{ display: "flex", justifyContent: "space-between", gap: 10, alignItems: "baseline" }}>
              <div>
                <p style={styles.sectionTitle}>Items</p>
                <p style={styles.hint}>Tap/click an item to add to cart.</p>
              </div>
              <span style={styles.badge}>{items.length} items</span>
            </div>

            <div className="itemsGrid" style={{ marginTop: 12 }}>
              {items.map((it) => (
                <div
                  key={it.id}
                  onClick={() => addToCart(it.id)}
                  style={{
                    ...styles.itemCard,
                    background: getItemGradient(it.name),
                    opacity: loading ? 0.75 : 1,
                  }}
                >
                  <div style={styles.emojiBox}>{getItemEmoji(it.name)}</div>
                  <div>
                    <p style={styles.itemName}>{it.name}</p>
                    <p style={styles.itemMeta}>Item ID: {it.id}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Cart panel */}
          <div className="card" style={styles.cardPad}>
            <div style={{ display: "flex", justifyContent: "space-between", gap: 10, alignItems: "baseline" }}>
              <div>
                <p style={styles.sectionTitle}>Cart</p>
                <p style={styles.hint}>Remove items if needed.</p>
              </div>
              <span style={styles.badge}>{cart?.cart_items?.length || 0} in cart</span>
            </div>

            {!cart?.cart_items?.length ? (
              <div style={{ marginTop: 12, color: "#6b7280", fontSize: 13 }}>Your cart is empty.</div>
            ) : (
              <div style={{ marginTop: 12 }}>
                {cart.cart_items.map((ci, idx) => {
                  const name = itemNameById.get(ci.item_id) || "Unknown";
                  return (
                    <div key={ci.id || idx} style={styles.cartRow}>
                      <div style={{ display: "flex", alignItems: "center", gap: 10 }}>
                        <div style={styles.emojiBox}>{getItemEmoji(name)}</div>
                        <div>
                          <div style={{ fontWeight: 900 }}>{name}</div>
                          <div style={{ fontSize: 12, color: "#6b7280" }}>Item #{ci.item_id}</div>
                        </div>
                      </div>

                      <button
                        onClick={() => removeFromCart(ci.item_id)}
                        disabled={loading}
                        style={{ ...styles.removeBtn, ...(loading ? styles.disabled : {}) }}
                      >
                        Remove
                      </button>
                    </div>
                  );
                })}
              </div>
            )}

            <div style={styles.small}>
              Tip: Use <b>Cart</b> button to view (cart_id, item_id) pairs.
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
